package test.operation;

public class increment_decrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1;
		int b=2;
		
	if(a++==b || b--==a++) {
		System.out.println("++a   :"+ ++a);
		System.out.println("a++  :"+a++);
	}
	else {System.out.println("b");}

	}

}
